  void confirmPurchase() {
    var total = getTotal().toStringAsFixed(2);
    var address = "${suburbController.text}, ${streetController.text}";
    ScaffoldMessenger.of(context).showSnackBar(SnackBar(
      content: Text(
        "Tu compra con un total de \$${total} llegará a tu domicilio $address en 30 minutos, disfruta de tus alimentos, Hilario Ramírez.",
        style: TextStyle(color: Colors.white),
      ),
      backgroundColor: Color(0xFFE63946),
      duration: Duration(seconds: 6),
      onVisible: () {
        Future.delayed(Duration(seconds: 6), () {
          setState(() {
            cart.clear(); // Limpia el carrito
          });
          Navigator.of(context).popUntil(
              (route) => route.isFirst); // Redirige a la pantalla principal
        });
      },
    ));
  }

  double getTotal() {
    return cart.fold(0, (total, current) => total + current['price']);
  }

