  void navigateToRestaurant(BuildContext context, String? restaurantName) {
    if (restaurantName == null || restaurantName.isEmpty) {
      debugPrint("El nombre del restaurante es nulo o vacío.");
      return;
    }

    // Inicializa la lista de platos si no existe
    if (!dishes.containsKey(restaurantName)) {
      dishes[restaurantName] = [];
    }

    final isUserRestaurant =
        userRestaurant != null && userRestaurant!['name'] == restaurantName;

    Navigator.push(
      context,
      MaterialPageRoute(
        builder: (context) => RestaurantPage(
          restaurantName: restaurantName,
          dishes: dishes[restaurantName]!, // Garantizamos que no será null
          onAddDish: (dish) {
            if (isUserRestaurant) {
              setState(() {
                dishes[restaurantName]!.add(dish);
              });
              debugPrint(
                  "Plato agregado exitosamente: ${dishes[restaurantName]}");
            }
          },

          onEditDish: (index, updatedDish) {
            if (isUserRestaurant) {
              setState(() {
                dishes[restaurantName]![index] = updatedDish;
              });
            }
          },
          onDeleteDish: (index) {
            if (isUserRestaurant) {
              setState(() {
                dishes[restaurantName]!.removeAt(index);
              });
            }
          },
          isUserRestaurant: isUserRestaurant,
        ),
      ),
    );
  }

