                            dishes[restaurant['name']] ?? [];
                        return Card(
                          margin: const EdgeInsets.symmetric(
                            vertical: 8,
                            horizontal: 16,
                          ),
                          child: ExpansionTile(
                            leading: const CircleAvatar(
                              backgroundColor: Color(0xFFE63946),
                              child: Icon(Icons.restaurant_menu,
                                  color: Colors.white),
                            ),
                            title: Text(
                              restaurant['name']!,
                              style: const TextStyle(
                                fontWeight: FontWeight.bold,
                                fontSize: 16,
                              ),
                            ),
                            subtitle: Text(
                              restaurant['type']!,
                              style: const TextStyle(
                                color: Colors.grey,
                              ),
                            ),
                            children: dishesForRestaurant.map((dish) {
                              return ListTile(
                                leading: Image.network(
                                  dish['imageUrl'] ?? '',
                                  width: 50,
                                  height: 50,
                                  fit: BoxFit.cover,
                                  errorBuilder: (context, error, stackTrace) =>
                                      const Icon(
                                    Icons.broken_image,
                                    size: 50,
                                    color: Colors.grey,
                                  ),
                                ),
                                title: Text(
                                  dish['name'],
                                  style: const TextStyle(
                                      fontWeight: FontWeight.bold),
                                ),
                                subtitle: Text(dish['description']),
                                trailing: Row(
                                  mainAxisSize: MainAxisSize.min,
                                  children: [
                                    Text(
                                      '\$${dish['price']}',
                                      style: const TextStyle(
                                        fontWeight: FontWeight.bold,
                                        color: Color(0xFFE63946),
                                        fontSize:
                                            17, // Tamaño de fuente aumentado para el precio
                                      ),
                                    ),
                                    IconButton(
                                      icon: Icon(Icons.shopping_cart,
                                          color: Color(0xFFE63946)),
                                      onPressed: () => addToCart(dish),
                                    )
                                  ],
                                ),
                              );
                            }).toList(),
                          ),
                        );
                      },
                    ),
            ),
          ],
        ),
      ),
      floatingActionButton: AnimatedBuilder(
        animation: _controller,
        builder: (context, child) {
          return Transform.scale(
            scale: _animation.value,
            child: FloatingActionButton(
              onPressed: viewCart,
              backgroundColor: const Color(0xFFE63946),
              child: const Icon(Icons.shopping_cart, color: Colors.white),
            ),
          );
        },
      ),

      floatingActionButtonLocation: FloatingActionButtonLocation
          .endFloat, // Ubicación estándar a la derecha
    );
  }
}

class RestaurantPage extends StatefulWidget {
  final String restaurantName;
  final List<Map<String, dynamic>> dishes;
  final void Function(Map<String, dynamic>) onAddDish;
  final void Function(int, Map<String, dynamic>) onEditDish;
  final void Function(int) onDeleteDish;
  final bool isUserRestaurant; // Nuevo parámetro

  const RestaurantPage({
    Key? key,
    required this.restaurantName,
    required this.dishes,
    required this.onAddDish,
    required this.onEditDish,
    required this.onDeleteDish,
    required this.isUserRestaurant, // Nuevo parámetro
  }) : super(key: key);

  @override
  _RestaurantPageState createState() => _RestaurantPageState();
}

class _RestaurantPageState extends State<RestaurantPage> {
  void showDishForm({Map<String, dynamic>? dish, int? index}) {
    final GlobalKey<FormState> _formKey = GlobalKey<FormState>();
    final TextEditingController nameController =
        TextEditingController(text: dish?['name']);
    final TextEditingController descriptionController =
        TextEditingController(text: dish?['description']);
    final TextEditingController priceController =
        TextEditingController(text: dish?['price']?.toString());
    final TextEditingController imageUrlController =
        TextEditingController(text: dish?['imageUrl']);

    showModalBottomSheet(
      context: context,
      isScrollControlled: true,
      shape: const RoundedRectangleBorder(
        borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
      ),
      builder: (context) {
        return Padding(
          padding: EdgeInsets.only(
            left: 16,
            right: 16,
            top: 16,
            bottom: MediaQuery.of(context).viewInsets.bottom,
          ),
          child: Form(
            key: _formKey,
            child: Column(
              mainAxisSize: MainAxisSize.min,
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                Text(
                  dish == null ? 'Agregar Producto' : 'Editar Producto',
                  textAlign: TextAlign.center,
                  style: const TextStyle(
                    fontFamily: 'Manrope',
                    fontSize: 20,
                    fontWeight: FontWeight.bold,
                    color: Color(0xFFE63946), // Color principal
                  ),
                ),
                const SizedBox(height: 16),
                TextFormField(
                  controller: nameController,
                  decoration: InputDecoration(
                    labelText: 'Nombre',
                    filled: true,
                    fillColor: const Color(0xFFFFE6E6),
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10),
                      borderSide: BorderSide.none,
                    ),
                  ),
                  validator: (value) {
                    if (value == null || value.isEmpty) {
                      return 'El nombre es obligatorio';
                    }
                    return null;
                  },
                ),
                const SizedBox(height: 16),
                TextFormField(
                  controller: descriptionController,
                  decoration: InputDecoration(
                    labelText: 'Descripción',
                    filled: true,
                    fillColor: const Color(0xFFFFE6E6),
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10),
                      borderSide: BorderSide.none,
                    ),
                  ),
                  maxLines: 3,
                  validator: (value) {
                    if (value == null || value.isEmpty) {
                      return 'La descripción es obligatoria';
                    }
                    return null;
                  },
                ),
                const SizedBox(height: 16),
                TextFormField(
                  controller: priceController,
                  decoration: InputDecoration(
                    labelText: 'Precio',
                    filled: true,
                    fillColor: const Color(0xFFFFE6E6),
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10),
                      borderSide: BorderSide.none,
                    ),
                  ),
                  keyboardType: TextInputType.number,
                  validator: (value) {
                    if (value == null || value.isEmpty) {
                      return 'El precio es obligatorio';
                    }
                    if (double.tryParse(value) == null) {
                      return 'Introduce un número válido';
                    }
                    return null;
                  },
                ),
                const SizedBox(height: 16),
                TextFormField(
                  controller: imageUrlController,
                  decoration: InputDecoration(
                    labelText: 'URL de Imagen',
                    filled: true,
                    fillColor: const Color(0xFFFFE6E6),
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10),
                      borderSide: BorderSide.none,
                    ),
                  ),
                  validator: (value) {
                    if (value == null || value.isEmpty) {
                      return 'La URL de la imagen es obligatoria';
                    }
                    return null;
                  },
                ),
                const SizedBox(height: 16),
                ElevatedButton(
                  onPressed: () {
                    if (_formKey.currentState!.validate()) {
                      final newDish = {
                        'name': nameController.text,
                        'description': descriptionController.text,
                        'price': double.tryParse(priceController.text) ?? 0.0,
                        'imageUrl': imageUrlController.text,
                      };

                      if (index == null) {
                        setState(() {
                          widget.dishes
                              .add(newDish); // Línea que fuerza el refresco
                        });
                      } else {
                        setState(() {
                          widget.dishes[index] =
                              newDish; // Línea que fuerza el refresco
                        });
                      }

                      Navigator.pop(context);
                    }
                  },
                  style: ElevatedButton.styleFrom(
                    backgroundColor: const Color(0xFFE63946), // Color principal
                    padding: const EdgeInsets.symmetric(vertical: 14),
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(10),
                    ),
                  ),
                  child: Text(
                    dish == null ? 'Agregar' : 'Actualizar',
                    style: const TextStyle(
                      fontFamily: 'Manrope',
                      fontWeight: FontWeight.bold,
                      fontSize: 16,
                      color: Colors.white,
                    ),
                  ),
                ),
              ],
            ),
          ),
        );
      },
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: const Color(0xFFE63946),
      ),
      body: Column(
        children: [
          Padding(
            padding: const EdgeInsets.all(16.0),
            child: Text(
              "Alimentos de ${widget.restaurantName}",
              style: const TextStyle(
                fontFamily: 'Manrope',
                fontSize: 20,
                fontWeight: FontWeight.bold,
                color: Color(0xFFE63946),
              ),
            ),
          ),
          Expanded(
            child: ListView.builder(
              key: ValueKey(
                  widget.dishes.length), // Fuerza la reconstrucción de la lista
              itemCount: widget.dishes.length,
              itemBuilder: (context, index) {
                final dish = widget.dishes[index];
                return Card(
                  margin: const EdgeInsets.symmetric(
                    vertical: 8,
                    horizontal: 16,
                  ),
                  child: ListTile(
                    leading: Image.network(
                      dish['imageUrl'] ?? '',
                      width: 50,
                      height: 50,
                      fit: BoxFit.cover,
                      errorBuilder: (context, error, stackTrace) =>
                          const Icon(Icons.broken_image),
                    ),
                    title: Text(dish['name']),
                    subtitle: Text('\$${dish['price']}'),
                    trailing: widget.isUserRestaurant
                        ? Row(
                            mainAxisSize: MainAxisSize.min,
                            children: [
                              IconButton(
                                icon: const Icon(Icons.edit),
                                color: Color(0xFFE53935), // Verde fresco
                                onPressed: () =>
                                    showDishForm(dish: dish, index: index),
                              ),
                              IconButton(
                                icon: const Icon(Icons.delete,
                                    color: Color(0xFFE63946)),
                                onPressed: () => confirmDelete(
                                    index), // Llama a la función de confirmación
                              ),
                            ],
                          )
                        : null,
                  ),
                );
              },
            ),
          ),
          if (widget
              .isUserRestaurant) // Botón de agregar producto solo si es del usuario
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: ElevatedButton.icon(
                onPressed: () =>
                    showDishForm(), // Tu función para abrir el formulario
                style: ElevatedButton.styleFrom(
                  backgroundColor:
                      Color(0xFFE63946), // Color del botón (naranja cálido)
                  padding: EdgeInsets.symmetric(vertical: 14),
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(10),
                  ),
                ),
                icon: const Icon(
                  Icons.add, // Icono de "Agregar"
                  color: Colors.white, // Color del icono
                ),
                label: const Text(
                  '',
                  style: TextStyle(
                    fontFamily: 'Manrope',
                    fontWeight: FontWeight.bold,
                    fontSize: 16,
                    color: Colors.white,
                  ),
                ),
              ),
            ),
        ],
      ),
    );
  }

  void confirmDelete(int index) {
    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text(
            'Confirmar Eliminación',
            style: TextStyle(
              fontFamily: 'Manrope',
              fontWeight: FontWeight.bold,
              fontSize: 18,
            ),
          ),
          content: const Text(
            '¿Estás seguro de que deseas eliminar este producto?',
            style: TextStyle(
              fontFamily: 'Manrope',
              fontSize: 16,
            ),
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop(); // Cierra el cuadro de diálogo
              },
              child: const Text(
                'Cancelar',
                style: TextStyle(
                  color: Colors.grey,
                  fontFamily: 'Manrope',
                  fontWeight: FontWeight.bold,
                ),
              ),
            ),
            ElevatedButton(
              onPressed: () {
                setState(() {
                  widget.dishes.removeAt(index); // Elimina el producto
                });
                Navigator.of(context).pop(); // Cierra el cuadro de diálogo
              },
              style: ElevatedButton.styleFrom(
                backgroundColor: const Color(0xFFE63946), // Color del botón
              ),
              child: const Text(
                'Eliminar',
                style: TextStyle(
                  color: Colors.white,
                  fontFamily: 'Manrope',
                  fontWeight: FontWeight.bold,
                ),
              ),
            ),
          ],
        );
      },
    );
  }
}
